import React, { useEffect, useState } from "react";
import { Link } from "@reach/router";
import ReactDOM from 'react-dom';
const Posts = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const getPosts = async () => {
      const resp = await fetch(
        "https://serverless-api.wyw6.workers.dev/api/posts"
      );
      const postsResp = await resp.json();
      setPosts(postsResp);
    };

    getPosts();
  }, []);
  ReactDOM.render(<Header />, document.getElementById('root'));

  return (
    <body>
      <div>


        <nav class="navbar nav nav-masthead fixed-top navbar-expand-sm bg-dark navbar-dark">
          <div class="container-fluid">
            <a class="navbar-brand" href="index.html" style="float:left; ">
              <img src="images/logo.png"></img>
            </a>
            <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#collapsibleNavbar">
              <span class="navbar-toggler-icon"></span>
            </button>
            <div class="collapse navbar-collapse" id="collapsibleNavbar" style="float:right">
              <ul class="navbar-nav ms-auto">
                <li class="nav-item">
                  <a class="nav-link" href="projects.html">Projects</a>
                </li>
                <li class="nav-item">
                  <a class="nav-link" href="research.html">Research</a>
                </li>
                <li class="nav-item">
                  <a class="nav-link" href="about.html">About Me</a>
                </li>
              </ul>
            </div>
          </div>
        </nav>
      </div>
      <div>
        <h1>DeMe</h1>
        {posts.map((post) => (
          <div key={post.id}>
            <h2>
              <Link to={`/posts/${post.id}`}>{post.title}</Link>
            </h2>
          </div>
        ))}
      </div>
    </body>
  );
};

const Header = () => {
  const headerStyle = {
    color: "white",
    backgroundColor: "DodgerBlue",
    padding: "10px",
    textAlign: "center",
    margin: "auto",
    width: "50%",
    fontSize: "50px"
  };
  const subHeaderStyle = {
    color: "white",
    background: "linear-gradient(rgba(250,0,0,0.5),transparent)",
    backgroundColor: "#15c06a",
    padding: "0px",
    fontFamily: "Sans-Serif",
    textAlign: "center",
    margin: "auto",
    width: "50%",
    fontSize: "20px"

  };
  return (
    <div>
      <h1 style={headerStyle}>Dalius</h1>
      <h2 style={subHeaderStyle}>Learn About Others</h2>

      <p>Add a little style!</p>
    </div>
  );
}


export default Posts;
