import React, { useEffect, useState } from "react";
import { Link } from "@reach/router";

import Toast from 'react-bootstrap/Toast';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';

const Posts = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const getPosts = async () => {
      const resp = await fetch(
        "https://serverless-api.wyw6.workers.dev/api/posts"
      );
      const postsResp = await resp.json();
      setPosts(postsResp);
    };

    getPosts();
  }, []);
  return (
    <body>
      <div>
        <h1>DeMe</h1>
        <h1 className="header">Welcome To React-Bootstrap</h1>

        {posts.map((post) => (
          <div key={post.id}>
            <Container className="p-3">
              <Container className="p-5 mb-4 bg-light rounded-3">
                <ExampleToast post={post}>
                </ExampleToast>
              </Container>
            </Container>
          </div>

        ))}
      </div>

    </body>

  );
};

const ExampleToast = ({ post }) => {
  const [show, toggleShow] = useState(true);

  return (
    <>
      <Toast show={show} onClose={() => toggleShow(false)}>
        <Toast.Header>
          <strong className="mr-auto">{post.title}</strong>
        </Toast.Header>

        <Toast.Body>
          <p >{daysFromToday(post.published_at)}</p>
          {post.text}
        </Toast.Body>
      </Toast>
    </>
  );
};

function daysFromToday(posted) {
  var today = Date.now();
  var parsed = Date.parse(posted);
  var time_difference = new Date(today - parsed);
  return (
    <>
      {time_difference.getDay()}
    </>);
}


export default Posts;
