import React, { useEffect, useState } from "react";
import { Link } from "@reach/router";
import Form from 'react-bootstrap/Form'
import Toast from 'react-bootstrap/Toast';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';

const Posts = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const getPosts = async () => {
      const resp = await fetch(
        "https://serverless-api.wyw6.workers.dev/api/posts"
      );
      const postsResp = await resp.json();
      setPosts(postsResp);
    };

    getPosts();
  }, []);
  return (
    <body >
      <div style={{ textAlign: "center" }}>
        <h1>Dalio</h1>
        <CreatePost></CreatePost>
        <PostForm></PostForm>
        {posts.map((post) => (
          <div key={post.id} >
            <Container className="p-3">
              <Container className="p-5 mb-4 bg-light rounded-3">
                <ExampleToast post={post}>
                </ExampleToast>
              </Container>
            </Container>
          </div>

        ))}
      </div>
    </body>

  );
};

const ExampleToast = ({ post }) => {
  const [show, toggleShow] = useState(true);

  return (
    <>
      <Toast show={show} onClose={() => toggleShow(false)}>
        <Toast.Header>
          <strong style={{ fontSize: "15px" }}>{post.title}</strong>
        </Toast.Header>

        <Toast.Body>
          <p style={{ fontSize: "11px" }}>{daysFromToday(post.published_at)} days ago</p>
          {post.text}
        </Toast.Body>
      </Toast>
    </>
  );
};

const CreatePost = () => {
  return (
    <Button variant="success" className="mr-1">
      Share a new thought
    </Button>
  );
}

const PostForm = () => {
  return (
    <Form style={{ width: "50%", textAlign: "center" }}>
      <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
        <Form.Label>Email address</Form.Label>
        <Form.Control type="email" placeholder="name@example.com" />
      </Form.Group>
      <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
        <Form.Label>Example textarea</Form.Label>
        <Form.Control as="textarea" rows={3} />
      </Form.Group>
    </Form>
  );
}

function daysFromToday(posted) {
  var today = Date.now();
  var parsed = Date.parse(posted);
  var time_difference = new Date(today - parsed);
  return (
    <>
      {time_difference.getDay()}
    </>);
}


export default Posts;
