import React, { useEffect, useState } from "react";
import { Link } from "@reach/router";
import Form from 'react-bootstrap/Form'
import Toast from 'react-bootstrap/Toast';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';

const Posts = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const getPosts = async () => {
      const resp = await fetch(
        "https://serverless-api.wyw6.workers.dev/api/posts"
      );
      const postsResp = await resp.json();
      setPosts(postsResp);
    };

    getPosts();
  }, []);
  return (
    <body >
      <div style={{ textAlign: "center" }}>
        <h1>Dalio</h1>
        <CreatePost></CreatePost>
        {posts.map((post) => (
          <div key={post.id} >
            <Container className="p-3">
              <Container className="p-5 mb-4 bg-light rounded-3">
                <ExampleToast post={post}>
                </ExampleToast>
              </Container>
            </Container>
          </div>

        ))}
      </div>
    </body>

  );
};

const ExampleToast = ({ post }) => {
  const [show, toggleShow] = useState(true);

  return (
    <>
      <Toast show={show} onClose={() => toggleShow(false)}>
        <Toast.Header>
          <strong style={{ fontSize: "15px" }}>{post.title}</strong>
        </Toast.Header>

        <Toast.Body style={{ textAlign: "left" }}>
          <p style={{ fontSize: "11px" }}>{daysFromToday(post.published_at)} days ago</p>
          {post.text}
        </Toast.Body>
      </Toast>
    </>
  );
};

const CreatePost = () => {
  const [showForm, showFormSet] = useState(false);
  const onClick = () => { showFormSet(true ^ showForm) }
  return (
    <div>
      <Button show={showForm} variant="success" className="mr-1" onClick={onClick}>
        Share a new thought
    </Button>
      { showForm ? <PostForm /> : null}
    </div>
  );
}


const PostForm = () => {

  return (
    <Form style={{ width: "30%", margin: "auto" }}>
      <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
        <Form.Control type="email" placeholder="Title" />
      </Form.Group>
      <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
        <Form.Control as="textarea" placeholder="My Thoughts" rows={2} />
      </Form.Group>
    </Form>
  );
}

function daysFromToday(posted) {
  var today = Date.now();
  var parsed = Date.parse(posted);
  var time_difference = new Date(today - parsed);
  return (
    <>
      {time_difference.getDay()}
    </>);
}


export default Posts;
